package feb6;

public class FlagPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char i;
		char j;
		char k;
		char l;
		for(i='A'; i<='E'; i++) {
			for(j='A'; j<=i; j++) {
				System.out.print(i);
			}
				System.out.println();
			}
		
				for(k='D'; k>='A'; k--) {
					for(l='A'; l<=k ; l++) {
						System.out.print(k);	
		}System.out.println();
				
			}
			
		}
	}
	
